ps2.v<pre><code>module ps2(
    input clk,
    input rst,
    input ps2_clk,
    input ps2_data,
    output reg [8:0] data
    );

    reg [1:0] clk_state;
    reg [3:0] r_state;// 计时
    reg [7:0] r_data; // 存储临时数据
    reg f,e; //是否特殊数据

    wire neg; // 探测ps2_clk的负边沿
    assign neg = ~clk_state[0] &amp; clk_state[1];

    always @(posedge clk or negedge rst) //初始化clk_state
        if(!rst)
            clk_state &lt;= 2'b00;
        else
            clk_state &lt;= {clk_state[0], ps2_clk};

    always @(posedge clk or negedge rst) begin //初始化数据
        if(!rst) begin
            r_state &lt;= 4'b0000;
            r_data &lt;= 8'b00000000;
            f &lt;= 1'b0;
            e &lt;= 1'b0;
            data &lt;= 9'b000000000;
        end
        else if(neg) begin  
            if(r_state &gt; 4'b1001) //读取完一整串数据后重置计时信号
                r_state &lt;=4'b0000; 
            else begin
                if(r_state &lt; 4'b1001&amp;&amp;r_state&gt;4'b0)
                    r_data[r_state-1]&lt;= ps2_data;  //存入ps2_data
                r_state &lt;= r_state + 1'b1;
            end
        end
        else if(r_state==4'b1010&amp;&amp;|r_data)begin
            if(r_data ==8'hf0) 
                f &lt;=1'b1;
            else if(r_data ==8'he0)
                e &lt;=1'b1;
            else
                if(f)begin  //代表断码，重置信号
                    data&lt;=9'b0;
                    f&lt;=1'b0;
                    e&lt;=1'b0;
                end
                else if(e)begin //在data头部输入1代表已经接受e信号
                    e&lt;=1'b0;
                    data &lt;={1'b1,r_data};
                end
                else
                    data &lt;= {1'b0,r_data};
            r_data &lt;= 8'b00000000;
        end
    end
endmodule
</code></pre>

ps2.dlc.v<pre><code>//防抖动以及输出上下左右
module ps2_dlc(
    input clk,
    input rst,
    input ps2_clk,
    input ps2_data,
    output reg [1:0]dir //direction:00up;01down;10left;11right
    );

    wire [8:0]data;
    reg [7:0]all; //[1:0]all 为11代表是up [3:2]代表down.....

    ps2 ps2(
        .clk(clk),
        .rst(rst),
        .ps2_clk(ps2_clk),
        .ps2_data(ps2_data),
        .data(data)
    );

    initial begin
        all&lt;=8'b00000000;
        dir&lt;=2'b11;
    end

    always @(posedge clk or negedge rst)begin
        if(!rst) begin
            all&lt;=8'b00000000;
        end
        else begin
            all[1:0]&lt;={all[0],data==9'h175}; //防抖动，连续输入两次才可以读入信号
            all[3:2]&lt;={all[2],data==9'h172};
            all[5:4]&lt;={all[4],data==9'h16b};
            all[7:6]&lt;={all[6],data==9'h174};
        end
    end

    always @(posedge clk or negedge rst)begin
        if(!rst)
            dir &lt;=2'b11;
        else begin
            case(all)//根据all数值输出方向信息
            8'b00000011:dir&lt;=2'b00;
            8'b00001100:dir&lt;=2'b01;
            8'b00110000:dir&lt;=2'b10;
            8'b11000000:dir&lt;=2'b11;
        default:;
        endcase
        end
    end
endmodule
</code></pre>

